class calculator:
    def __init__(self,value_1,value_2):
        self.value_1 = value_1
        self.value_2 = value_2

    def add(a,b):
        addition = a + b
        return addition

    def subtract(a, b):
        subtract = a - b
        return subtract

    def multiply(a,b):
        multiply = a * b
        return multiply

    def divide(a,b):
        divide = a / b
        return divide